class dog {
	String name;
	String breed;
	int cost;
	String color;
	
	void run (){
		System.out.println("dog runs");
	}
	void eat() {
		System.out.println("dog eats");
	}
}
public class Encapsulationcodes1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		dog d1 = new dog();
		d1.name="chinni";
		d1.cost=5000;
		d1.breed="cb";
        d1.color="brown";
        
        System.out.println(d1.name);
        System.out.println(d1.cost);
        System.out.println(d1.breed);
        System.out.println(d1.color);
        
        d1.eat();
        d1.run();
        		
	}

}

// as seen in the above program we are able to access all the properties 


