package javaprograms;
//             Explict type casting
public class code2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      double d1 = 10;
      System.out.println("Double data is :"+d1);
      float f1 = (float)d1;
      System.out.println("float data is :"+d1);
      
    byte b1 = (byte)d1;
    System.out.println("byte data is: "+d1);
    short s1 = (short)d1;
  System.out.println("short data is :"+d1);
  int i1 = (int)d1;
  System.out.println("int data is :"+d1);
  long l1 = (long)d1;
  System.out.println("long data is"+d1);
  System.out.println("-----------------");
  byte a = 10;
	System.out.println("byte data is :"+a);
  short s =a;// implict type casting
  System.out.println("Short data is :"+a);
  int i = a;// implict type casting
  System.out.println("Int data is :"+a);
  long l = a;// implict type casting
  System.out.println("Long data is :"+a);
   float f =a;// implict type casting
   System.out.println("float data is :"+a);
  	 double d =a;// implict type casting
  	 System.out.println("double data is :"+a);
  
      
	}

}
